use std::collections::HashSet;

const INPUT: &[u8] = b"";

fn main() {
    dbg!(after_marker(INPUT, 4));
    dbg!(after_marker(INPUT, 14));
}

fn after_marker(input: &[u8], window_size: usize) -> Option<usize> {
    for (idx, x) in input.windows(window_size).enumerate() {
        let distinct: HashSet<_> = HashSet::from_iter(x.iter());
        if distinct.len() == window_size {
            return Some(idx + window_size);
        }
    }
    None
}
